package sumitdemo;

public class Swapingnumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=5;
		int b=8;
		System.out.println("Before swaping,, The numbers are "+b+" and "+a);
		int x;
		x=b;
		b=a;
		a=x;
		System.out.println("after swaping,, The numbers are "+b+" and "+a);
	}
	



	}


